<script>
import { ref } from '@nuxtjs/composition-api'
import { useMotion } from '@vueuse/motion'
export default {
  name: 'GraphcAssociations',
  setup() {
    const person1 = ref()
    const person2 = ref()

    const persons = [person1, person2]

    persons.forEach((person, index) => {
      const { variant } = useMotion(person, {
        initial: {
          scale: 1,
          y: 15,
          opacity: 0,
        },
        enter: {
          y: 0,
          opacity: 1,
          transition: {
            type: 'spring',
            stiffness: 200,
            damping: 20,
            onComplete: () => {
              variant.value = 'levitate'
            },
          },
        },
        levitate: {
          y: 10 + index * 5,
          transition: {
            duration: 1500 + index * 500,
            repeat: Infinity,
            ease: 'easeInOut',
            repeatType: 'mirror',
          },
        },
      })
    })

    return {
      person1,
      person2,
    }
  },
}
</script>

